(28, 6, 'Ideal Gas Law Calculator', 'ideal-gas-law-calculator', '<form id=\"gas-law-form\" class=\"form\">\r\n            <div class=\"form-group\">\r\n                <label for=\"pressure\">Pressure (P)</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"pressure\" placeholder=\"Pressure in atm\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"volume\">Volume (V)</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"volume\" placeholder=\"Volume in L\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"temperature\">Temperature (T)</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"temperature\" placeholder=\"Temperature in K\">\r\n            </div>\r\n        </form>\r\n        <div class=\"result mt-3\">\r\n            <h4>Result:</h4>\r\n            <p id=\"result-text\">Fill in the values above to see the result.</p>\r\n        </div>\r\n    \r\n    <script>\r\n        const pressureInput = document.getElementById(\'pressure\');\r\n        const volumeInput = document.getElementById(\'volume\');\r\n        const temperatureInput = document.getElementById(\'temperature\');\r\n        const resultText = document.getElementById(\'result-text\');\r\n\r\n        function calculateIdealGasLaw(p, v, t) {\r\n            const R = 0.0821; // Ideal gas constant in atm L / mol K\r\n            const n = 1; // Assume 1 mole of gas\r\n            return (n * R * t) / (p * v);\r\n        }\r\n\r\n        function updateResult() {\r\n            const pressure = parseFloat(pressureInput.value);\r\n            const volume = parseFloat(volumeInput.value);\r\n            const temperature = parseFloat(temperatureInput.value);\r\n\r\n            if (!isNaN(pressure) && !isNaN(volume) && !isNaN(temperature)) {\r\n                const result = calculateIdealGasLaw(pressure, volume, temperature);\r\n          