(78, 6, 'Charles Law Calculator', 'charles-law-calculator', '<form>\r\n            <div class=\"form-group\">\r\n                <label for=\"initial-volume\">Initial Volume (V1, L):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"initial-volume\" placeholder=\"Enter initial volume\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"initial-temperature\">Initial Temperature (T1, K):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"initial-temperature\" placeholder=\"Enter initial temperature\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"final-temperature\">Final Temperature (T2, K):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"final-temperature\" placeholder=\"Enter final temperature\" step=\"0.01\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Resulting Volume (V2, L):</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n  \r\n\r\n    <script>\r\n        // Function to calculate the resulting volume and update the result field\r\n        function calculateCharlesLaw() {\r\n            const initialVolume = parseFloat(document.getElementById(\"initial-volume\").value);\r\n            const initialTemperature = parseFloat(document.getElementById(\"initial-temperature\").value);\r\n            const finalTemperature = parseFloat(document.getElementById(\"final-temperature\").value);\r\n\r\n            if (!isNaN(initialVolume) && !isNaN(initialTemperature) && !isNaN(finalTemperature) && initialTemperature > 0 && finalTemperature > 0) {\r\n                const resultingVolume = (initialVolume * finalTemperature) / initialTemperature;\r\n                document.getElementById(\"result\").textContent = `Resulting Volume: ${resultingVolume.toFixed(2)} L`;\r\n            }