(82, 7, 'USB Data Transfer Calculator', 'usb-data-transfer-calculator', ' <form>\r\n            <div class=\"form-group\">\r\n                <label for=\"data-size\">Data Size (GB):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"data-size\" placeholder=\"Enter data size\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"transfer-speed\">Transfer Speed (MB/s):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"transfer-speed\" placeholder=\"Enter transfer speed\" step=\"0.01\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Time to Transfer:</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n   \r\n\r\n    <script>\r\n        // Function to calculate the time to transfer data and update the result field\r\n        function calculateTransferTime() {\r\n            const dataSize = parseFloat(document.getElementById(\"data-size\").value);\r\n            const transferSpeed = parseFloat(document.getElementById(\"transfer-speed\").value);\r\n\r\n            if (!isNaN(dataSize) && !isNaN(transferSpeed) && transferSpeed > 0) {\r\n                const transferTime = dataSize * 1024 / transferSpeed; // Convert GB to MB\r\n                document.getElementById(\"result\").textContent = `Time to Transfer: ${transferTime.toFixed(2)} seconds`;\r\n            } else {\r\n                document.getElementById(\"result\").textContent = \"\";\r\n            }\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"data-size\").addEventListener(\"input\", calculateTransferTime);\r\n        document.getElementById(\"transfer-speed\").addEventListener(\"input\", calculateTransferTime);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n            const result = document.getElementById(\"result\").textContent;\r\n            if (result) {\r\n                window.open(\"\", \"_blank\");\r\n                const resultWindow = window.open(\"\", \"_blank\");\r\n                resultWindow.document.write(\"<h1>USB Data Transfer Calculator Result:</h1>\");\r\n                resultWindow.document.write(`<p>${result}</p>`);\r\n                resultWindow.document.close();\r\n            }\r\n        }\r\n\r\n        // Prevent the form from submitting on Enter key press\r\n        document.addEventListener(\"keydown\", function (e) {\r\n            if (e.key === \"Enter\") {\r\n                e.preventDefault();\r\n                openResultWindow();\r\n            }\r\n        });\r\n    </script>', '', '', '', '', 52, ''),
(83, 7, 'Data Download Time Calculator', 'data-download-time-calculator', '<form>\r\n            <div class=\"form-group\">\r\n                <label for=\"file-size\">File Size (MB):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"file-size\" placeholder=\"Enter file size\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"download-speed\">Download Speed (Mbps):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"download-speed\" placeholder=\"Enter download speed\" step=\"0.01\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Estimated Download Time:</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n    \r\n\r\n    <script>\r\n        // Function to calculate download time and update the result field\r\n        function calculateDownloadTime() {\r\n            const fileSize = parseFloat(document.getElementById(\"file-size\").value);\r\n            const downloadSpeed = parseFloat(document.getElementById(\"download-speed\").value);\r\n\r\n            if (!isNaN(fileSize) && !isNaN(downloadSpeed) && downloadSpeed > 0) {\r\n                const downloadTime = (fileSize * 8) / (downloadSpeed); // Convert MB to bits\r\n                document.getElementById(\"result\").textContent = `Estimated Download Time: ${downloadTime.toFixed(2)} seconds`;\r\n            } else {\r\n                document.getElementById(\"result\").textContent = \"\";\r\n            }\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"file-size\").addEventListener(\"input\", calculateDownloadTime);\r\n        document.getElementById(\"download-speed\").addEventListener(\"input\", calculateDownloadTime);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n       