(84, 7, 'Pixels Per Inch PPI Calculator', 'pixels-per-inch-ppi-calculator', '<form>\r\n            <div class=\"form-group\">\r\n                <label for=\"screen-width\">Screen Width (inches):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"screen-width\" placeholder=\"Enter screen width\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"screen-height\">Screen Height (inches):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"screen-height\" placeholder=\"Enter screen height\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"screen-diagonal\">Screen Diagonal Size (inches):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"screen-diagonal\" placeholder=\"Enter screen diagonal size\" step=\"0.01\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Pixels per Inch (PPI):</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n    \r\n    <script>\r\n        // Function to calculate PPI and update the result field\r\n        function calculatePPI() {\r\n            const screenWidth = parseFloat(document.getElementById(\"screen-width\").value);\r\n            const screenHeight = parseFloat(document.getElementById(\"screen-height\").value);\r\n            const screenDiagonal = parseFloat(document.getElementById(\"screen-diagonal\").value);\r\n\r\n            if (!isNaN(screenWidth) && !isNaN(screenHeight) && !isNaN(screenDiagonal) && screenDiagonal > 0) {\r\n                const diagonalResolution = Math.sqrt(Math.pow(screenWidth, 2) + Math.pow(screenHeight, 2));\r\n                const ppi = diagonalResolution / screenDiagonal;\r\n                document.getElementById(\"result\").textContent = `Pixels per Inch (PPI): ${ppi.toFixed(2)}`;\r\n            } else {\r\n                document.getElementById(\"result\").textContent = \"\";\r\n            }\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"screen-width\").addEventListener(\"input\", calculatePPI);\r\n        document.getElementById(\"screen-height\").addEventListener(\"input\", calculatePPI);\r\n        document.getElementById(\"screen-diagonal\").addEventListener(\"input\", calculatePPI);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n            const result = document.getElementById(\"result\").textContent;\r\n            if (result) {\r\n                window.open(\"\", \"_blank\");\r\n                const resultWindow = window.open(\"\", \"_blank\");\r\n                resultWindow.document.write(\"<h1>Pixels per Inch (PPI) Calculator Result:</h1>\");\r\n                resultWindow.document.write(`<p>${result}</p>`);\r\n                resultWindow.document.close();\r\n            }\r\n        }\r\n\r\n        // Prevent the form from submitting on Enter key press\r\n        document.addEventListener(\"keydown\", function (e) {\r\n            if (e.key === \"Enter\") {\r\n                e.preventDefault();\r\n                openResultWindow();\r\n            }\r\n        });\r\n    </script>\r\n', '', '', '', '', 61, ''),
(85, 3, 'Mortgage Calculator', 'mortgage-calculator', '   <form>\r\n            <div class=\"form-group\">\r\n                <label for=\"loan-amount\">Loan Amount ($):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"loan-amount\" placeholder=\"Enter loan amount\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"interest-rate\">Annual Interest Rate (%):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"interest-rate\" placeholder=\"Enter annual interest rate\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"loan-term\">Loan Term (years):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"loan-term\" placeholder=\"Enter loan term in years\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Monthly Payment:</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n   \r\n    <script>\r\n        // Function to calculate monthly mortgage payment and update the result field\r\n        function calculateMortgagePayment() {\r\n            const loanAmount = parseFloat(document.getElementById(\"loan-amount\").value);\r\n            const interestRate = parseFloat(document.getElementById(\"interest-rate\").value) / 100 / 12; // Monthly interest rate\r\n            const loanTerm = parseFloat(document.getElementById(\"loan-term\").value) * 12; // Monthly loan term\r\n\r\n            if (!isNaN(loanAmount) && !isNaN(interestRate) && !isNaN(loanTerm)) {\r\n                const monthlyPayment = (loanAmount * interestRate) / (1 - Math.pow(1 + interestRate, -loanTerm));\r\n                document.getElementById(\"result\").textContent = `Monthly Payment: $${monthlyPayment.toFixed(2)}`;\r\n            } else {\r\n                document.getElementById(\"result\").textContent = \"\";\r\n            }\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"loan-amount\").addEventListener(\"input\", calculateMortgagePayment);\r\n        document.getElementById(\"interest-rate\").addEventListener(\"input\", calculateMortgagePayment);\r\n        document.getElementById(\"loan-term\").addEventListener(\"input\", calculateMortgagePayment);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n            const result = document.getElementById(\"result\").textContent;\r\n            if (result) {\r\n                window.open(\"\", \"_blank\");\r\n                const resultWindow = window.open(\"\", \"_blank\");\r\n                resultWindow.document.write(\"<h1>Mortgage Calculator Result:</h1>\");\r\n                resultWindow.document.write(`<p>${result}</p>`);\r\n                resultWindow.document.close();\r\n            }\r\n        }\r\n\r\n        // Prevent the form from submitting on Enter key press\r\n        document.addEventListener(\"keydown\", function (e) {\r\n            if (e.key === \"Enter\") {\r\n                e.preventDefault();\r\n                openResultWindow();\r\n            }\r\n        });\r\n    </script>', '', '', '', '', 64, ''),
(86, 3, 'Commission Calculator', 'commission-calculator', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <title>Commission Calculator</title>\r\n    <link rel=\"stylesheet\" href=\"https://maxcdn.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css\">\r\n</head>\r\n<body>\r\n    <div class=\"container mt-5\">\r\n        <h1>Commission Calculator</h1>\r\n        <form>\r\n            <div class=\"form-group\">\r\n                <label for=\"sales-amount\">Sales Amount ($):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"sales-amount\" placeholder=\"Enter sales amount\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"commission-rate\">Commission Rate (%):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"commission-rate\" placeholder=\"Enter commission rate\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Commission Amount:</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n    </div>\r\n\r\n    <script>\r\n        // Function to calculate commission amount and update the result field\r\n        function calculateCommission() {\r\n            const salesAmount = parseFloat(document.getElementById(\"sales-amount\").value);\r\n            const commissionRate = parseFloat(document.getElementById(\"commission-rate\").value) / 100;\r\n\r\n            if (!isNaN(salesAmount) && !isNaN(commissionRate)) {\r\n                const commissionAmount = salesAmount * commissionRate;\r\n                document.getElementById(\"result\").textContent = `Commission Amount: $${commissionAmount.toFixed(2)}`;\r\n            } else {\r\n                document.getElementById(\"result\").textContent = \"\";\r\n            }\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"sales-amount\").addEventListener(\"input\", calculateCommission);\r\n        document.getElementById(\"commission-rate\").addEventListener(\"input\", calculateCommission);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n            const result = document.getElementById(\"result\").textContent;\r\n            if (result) {\r\n                window.open(\"\", \"_blank\");\r\n                const resultWindow = window.open(\"\", \"_blank\");\r\n                resultWindow.document.write(\"<h1>Commission Calculator Result:</h1>\");\r\n                resultWindow.document.write(`<p>${result}</p>`);\r\n                resultWindow.document.close();\r\n            }\r\n        }\r\n\r\n        // Prevent the form from submitting on Enter key press\r\n        document.addEventListener(\"keydown\", function (e) {\r\n            if (e.key === \"Enter\") {\r\n                e.preventDefault();\r\n                openResultWindow();\r\n            }\r\n        });\r\n    </script>\r\n\r\n    <script src=\"https://code.jquery.com/jquery-3.5.1.slim.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/@popperjs/core@2.5.3/dist/umd/popper.min.js\"></script>\r\n    <script src=\"https://maxcdn.bootstrapcdn.com/bootstrap/4.5.2/js/bootstrap.min.js\"></script>\r\n</body>\r\n</html>\r\n', '', 'Commission Calculator', '', '', 92, ''),
(87, 1, 'Fat Intake Calculator', 'fat-intake-calculator', ' <form>\r\n            <div class=\"form-group\">\r\n                <label for=\"calories\">Calories Consumed:</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"calories\" placeholder=\"Enter calories consumed\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"fat-grams\">Grams of Fat Consumed:</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"fat-grams\" placeholder=\"Enter grams of fat consumed\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Fat Intake Percentage:</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n   \r\n    <script>\r\n        // Function to calculate fat intake percentage and update the result field\r\n        function calculateFatIntake() {\r\n            const caloriesConsumed = parseFloat(document.getElementById(\"calories\").value);\r\n            const fatGramsConsumed = parseFloat(document.getElementById(\"fat-grams\").value);\r\n\r\n            if (!isNaN(caloriesConsumed) && !isNaN(fatGramsConsumed) && fatGramsConsumed >= 0) {\r\n                const fatIntakePercentage = (fatGramsConsumed * 9 / caloriesConsumed) * 100;\r\n                document.getElementById(\"result\").textContent = `Fat Intake Percentage: ${fatIntakePercentage.toFixed(2)}%`;\r\n            } else {\r\n                document.getElementById(\"result\").textContent = \"\";\r\n            }\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"calories\").addEventListener(\"input\", calculateFatIntake);\r\n        document.getElementById(\"fat-grams\").addEventListener(\"input\", calculateFatIntake);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n            const result = document.getElementById(\"result\").textContent;\r\n            if (result) {\r\n                window.open(\"\", \"_blank\");\r\n                const resultWindow = window.open(\"\", \"_blank\");\r\n                resultWindow.document.write(\"<h1>Fat Intake Calculator Result:</h1>\");\r\n                resultWindow.document.write(`<p>${result}</p>`);\r\n                resultWindow.document.close();\r\n            }\r\n        }\r\n\r\n        // Prevent the form from submitting on Enter key press\r\n        document.addEventListener(\"keydown\", function (e) {\r\n            if (e.key === \"Enter\") {\r\n                e.preventDefault();\r\n                openResultWindow();\r\n            }\r\n        });\r\n    </script>', '', '', '', '', 175, ''),
(88, 3, 'GDP Calculator', 'gdp-calculator', '<form>\r\n            <div class=\"form-group\">\r\n                <label for=\"consumption\">Consumption (C):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"consumption\" placeholder=\"Enter consumption\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"investment\">Investment (I):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"investment\" placeholder=\"Enter investment\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"government\">Government Spending (G):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"government\" placeholder=\"Enter government spending\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"exports\">Exports (X):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"exports\" placeholder=\"Enter exports\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"imports\">Imports (M):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"imports\" placeholder=\"Enter imports\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Gross Domestic Product (GDP):</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n  \r\n    <script>\r\n        // Function to calculate GDP and update the result field\r\n        function calculateGDP() {\r\n            const consumption = parseFloat(document.getElementById(\"consumption\").value);\r\n            const investment = parseFloat(document.getElementById(\"investment\").value);\r\n            const government = parseFloat(document.getElementById(\"government\").value);\r\n            const exportsValue = parseFloat(document.getElementById(\"exports\").value);\r\n            const importsValue = parseFloat(document.getElementById(\"imports\").value);\r\n\r\n            if (!isNaN(consumption) && !isNaN(investment) && !isNaN(government) && !isNaN(exportsValue) && !isNaN(importsValue)) {\r\n                const GDP = consumption + investment + government + (exportsValue - importsValue);\r\n                document.getElementById(\"result\").textContent = `Gross Domestic Product (GDP): $${GDP.toFixed(2)}`;\r\n            } else {\r\n                document.getElementById(\"result\").textContent = \"\";\r\n            }\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"consumption\").addEventListener(\"input\", calculateGDP);\r\n        document.getElementById(\"investment\").addEventListener(\"input\", calculateGDP);\r\n        document.getElementById(\"government\").addEventListener(\"input\", calculateGDP);\r\n        document.getElementById(\"exports\").addEventListener(\"input\", calculateGDP);\r\n        document.getElementById(\"imports\").addEventListener(\"input\", calculateGDP);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n            const result = document.getElementById(\"result\").textContent;\r\n            if (result) {\r\n                window.open(\"\", \"_blank\");\r\n                const resultWindow = window.open(\"\", \"_blank\");\r\n                resultWindow.document.write(\"<h1>GDP Calculator Result:</h1>\");\r\n                resultWindow.document.write(`<p>${result}</p>`);\r\n                resultWindow.document.close();\r\n            }\r\n        }\r\n\r\n        // Prevent the form from submitting on Enter key press\r\n        document.addEventListener(\"keydown\", function (e) {\r\n            if (e.key === \"Enter\") {\r\n                e.preventDefault();\r\n                openResultWindow();\r\n            }\r\n        });\r\n    </script>\r\n', '', '', '