(100, 5, 'Heat Index Calculator', 'heat-index-calculator', '\r\n        <form>\r\n            <div class=\"form-group\">\r\n                <label for=\"temperature\">Temperature (F):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"temperature\" placeholder=\"Enter temperature (F)\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"relative-humidity\">Relative Humidity (%):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"relative-humidity\" placeholder=\"Enter relative humidity (%)\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Heat Index (F):</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n\r\n\r\n    <script>\r\n        // Function to calculate the heat index and update the result field\r\n        function calculateHeatIndex() {\r\n            const temperature = parseFloat(document.getElementById(\"temperature\").value);\r\n            const relativeHumidity = parseFloat(document.getElementById(\"relative-humidity\").value);\r\n\r\n            if (!isNaN(temperature) && !isNaN(relativeHumidity) && temperature >= 80 && relativeHumidity >= 40) {\r\n                const heatIndex = calculateHeatIndexFormula(temperature, relativeHumidity);\r\n                document.getElementById(\"result\").textContent = `Heat Index (F): ${heatIndex.toFixed(2)}`;\r\n            } else {\r\n                document.getElementById(\"result\").textContent = \"\";\r\n            }\r\n        }\r\n\r\n        // Formula to calculate the heat index\r\n        function calculateHeatIndexFormula(temperature, relativeHumidity) {\r\n            const c1 = -42.379;\r\n            const c2 = 2.04901523;\r\n            const c3 = 10.14333127;\r\n            const c4 = -0.22475541;\r\n            const c5 = -6.83783e-03;\r\n            const c6 = -5.481717e-02;\r\n            const c7 = 1.22874e-03;\r\n            const c8 = 8.5282e-04;\r\n            const c9 = -1.99e-06;\r\n\r\n            const heatIndex = c1 + (c2 * temperature) + (c3 * relativeHumidity) + (c4 * temperature * relativeHumidity)\r\n                + (c5 * temperature * temperature) + (c6 * relativeHumidity * relativeHumidity)\r\n                + (c7 * temperature * temperature * relativeHumidity) + (c8 * temperature * relativeHumidity * relativeHumidity)\r\n                + (c9 * temperature * temperature * relativeHumidity * relativeHumidity);\r\n\r\n            return heatIndex;\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"temperature\").addEventListener(\"input\", calculateHeatIndex);\r\n        document.getElementById(\"relative-humidity\").addEventListener(\"input\", calculateHeatIndex);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n            const result = document.getElementById(\"result\").textContent;\r\n            if (result) {\r\n                window.open(\"\", \"_blank\");\r\n                const resultWindow = window.open(\"\", \"_blank\");\r\n                resultWindow.document.write(\"<h1>Heat Index Calculator Result:</h1>\");\r\n                resultWindow.document.write(`<p>${result}</p>`);\r\n                resultWindow.document.close();\r\n            }\r\n        }\r\n\r\n        // Prevent the form from submitting on Enter key press\r\n        document.addEventListener(\"keydown\", function (e) {\r\n            if (e.key === \"Enter\") {\r\n                e.preventDefault();\r\n                openResultWindow();\r\n            }\r\n        });\r\n    </script>', '', '', '', '', 94, ''),
