n    <script>\r\n        function switchTab(tabId) {\r\n            document.querySelectorAll(\'.content\').forEach(content => {\r\n                content.style.display = \'none\';\r\n            });\r\n            document.getElementById(tabId).style.display = \'block\';\r\n\r\n            document.querySelectorAll(\'.tab\').forEach(tab => {\r\n                tab.classList.remove(\'active\');\r\n            });\r\n            document.querySelector(`.tab[onclick=\"switchTab(\'${tabId}\')\"]`).classList.add(\'active\');\r\n        }\r\n\r\n        function calculateTDS() {\r\n            const inputs = document.querySelectorAll(\'#water-analysis input\');\r\n            let tds = 0;\r\n            inputs.forEach(input => {\r\n                tds += parseFloat(input.value) || 0;\r\n            });\r\n            document.getElementById(\'tds-result\').textContent = tds.toFixed(2);\r\n        }\r\n\r\n        function calculateECtoTDS() {\r\n            const k = parseFloat(document.getElementById(\'conversion-factor\').value) || 0;\r\n            const ec = parseFloat(document.getElementById(\'electrical-conductivity\').value) || 0;\r\n            const tds = k * ec;\r\n            document.getElementById(\'ec-tds-result\').textContent = tds.toFixed(2);\r\n        }\r\n    </script>\r\n</body>\r\n</html>\r\n', '', '', '', '', 1, '');
