(51, 1, 'Steps to Miles Calculator', 'steps-to-miles-calculator', '     <form>\r\n            <div class=\"form-group\">\r\n                <label for=\"steps\">Enter Number of Steps:</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"steps\" placeholder=\"Enter steps\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Result:</label>\r\n            <input type=\"text\" class=\"form-control\" id=\"result\" disabled>\r\n        </div>\r\n\r\n    <!-- Include Bootstrap JS and jQuery -->\r\n    <script src=\"https://code.jquery.com/jquery-3.5.1.slim.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/@popperjs/core@2.5.3/dist/umd/popper.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/bootstrap@4.5.2/dist/js/bootstrap.min.js\"></script>\r\n\r\n    <script>\r\n        // Function to calculate miles from steps\r\n        function calculateMiles(steps) {\r\n            return steps / 2000; // Assuming an average of 2000 steps per mile\r\n        }\r\n\r\n        // Function to update the result in real-time\r\n        function updateResult() {\r\n            const stepsInput = document.getElementById(\'steps\');\r\n            const resultInput = document.getElementById(\'result\');\r\n            const steps = parseFloat(stepsInput.value);\r\n\r\n            if (!isNaN(steps)) {\r\n                const miles = calculateMiles(steps);\r\n                resultInput.value = miles.toFixed(2) + \' miles\';\r\n            } else {\r\n                resultInput.value = \'\';\r\n            }\r\n        }\r\n\r\n        // Add an event listener to update the result while typing\r\n        const stepsInput = document.getElementById(\'steps\');\r\n        stepsInput.addEventListener(\'input\', updateResult);\r\n    </script>', '', '', '', '', 85, ''),
