onst x2Input = document.getElementById(\'x2\');\r\n            const y2Input = document.getElementById(\'y2\');\r\n            const resultInput = document.getElementById(\'result\');\r\n\r\n            const x1 = parseFloat(x1Input.value);\r\n            const y1 = parseFloat(y1Input.value);\r\n            const x2 = parseFloat(x2Input.value);\r\n            const y2 = parseFloat(y2Input.value);\r\n\r\n            if (!isNaN(x1) && !isNaN(y1) && !isNaN(x2) && !isNaN(y2)) {\r\n                const distance = calculateDistance(x1, y1, x2, y2);\r\n                resultInput.value = distance.toFixed(2);\r\n            } else {\r\n                resultInput.value = \'\';\r\n            }\r\n        }\r\n\r\n        // Add event listeners to update the result while typing\r\n        const inputs = document.querySelectorAll(\'input\');\r\n        inputs.forEach(input => {\r\n            input.addEventListener(\'input\', updateResult);\r\n        });\r\n    </script>', '', '', '', '', 45, ''),
(53, 2, 'Fraction to Decimal Calculator', 'fraction-to-decimal-calculator', '        <form>\r\n            <div class=\"form-group\">\r\n                <label for=\"numerator\">Enter Numerator:</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"numerator\" placeholder=\"Enter numerator\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"denominator\">Enter Denominator:</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"denominator\" placeholder=\"Enter denominator\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Result:</label>\r\n            <input type=\"text\" class=\"form-control\" id=\"result\" disabled>\r\n        </div>\r\n    \r\n    <!-- Include Bootstrap JS and jQuery -->\r\n    <script src=\"https://code.jquery.com/jquery-3.5.1.slim.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/@popperjs/core@2.5.3/dist/umd/popper.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/bootstrap@4.5.2/dist/js/bootstrap.min.js\"></script>\r\n\r\n    <script>\r\n        // Function to calculate decimal from fraction\r\n        function fractionToDecimal(numerator, denominator) {\r\n            if (denominator === 0) {\r\n                return \"Undefined\";\r\n            }\r\n            return (numerator / denominator).toFixed(2);\r\n        }\r\n\r\n        // Function to update the result in real-time\r\n        function updateResult() {\r\n            const numeratorInput = document.getElementById(\'numerator\');\r\n            const denominatorInput = document.getElementById(\'denominator\');\r\n            const resultInput = document.getElementById(\'result\');\r\n\r\n            const numerator = parseFloat(numeratorInput.value);\r\n            const denominator = parseFloat(denominatorInput.value);\r\n\r\n            if (!isNaN(numerator) && !isNaN(denominator)) {\r\n                const result = fractionToDecimal(numerator, denominator);\r\n                resultInput.value = result;\r\n            } else {\r\n                resultInput.value = \'\';\r\n            }\r\n        }\r\n\r\n        // Add event listeners to update the result while typing\r\n        const inputs = document.querySelectorAll(\'input\');\r\n        inputs.forEach(input => {\r\n            input.addEventListener(\'input\', updateResult);\r\n        });\r\n    </script>', '', '', '', '', 41, ''),
(54, 2, 'Decimal to Fraction Calculator', 'decimal-to-fraction-calculator', '<form>\r\n            <div class=\"form-group\">\r\n                <label for=\"decimal\">Enter Decimal:</label>\r\n                <input type=\"number\" step=\"0.01\" class=\"form-control\" id=\"decimal\" placeholder=\"Enter decimal\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Result:</label>\r\n            <input type=\"text\" class=\"form-control\" id=\"result\" disabled>\r\n        </div>\r\n  \r\n    <!-- Include Bootstrap JS and jQuery -->\r\n    <script src=\"https://code.jquery.com/jquery-3.5.1.slim.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/@popperjs/core@2.5.3/dist/umd/popper.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/bootstrap@4.5.2/dist/js/bootstrap.min.js\"></script>\r\n\r\n    <script>\r\n        // Function to calculate greatest common divisor (GCD)\r\n        function gcd(a, b) {\r\n            if (b === 0) {\r\n                return a;\r\n            }\r\n            return gcd(b, a % b);\r\n        }\r\n\r\n        // Function to convert a decimal to a simplified fraction\r\n        function decimalToFraction(decimal) {\r\n            const epsilon = 1.0 / 10000; // A small number to handle rounding errors\r\n            let lower = 1;\r\n            let upper = 1;\r\n\r\n            while (Math.abs(decimal - (upper / lower)) > epsilon) {\r\n                if (decimal > (upper / lower)) {\r\n                    lower++;\r\n                } else {\r\n                    upper++;\r\n                }\r\n            }\r\n\r\n            const divisor = gcd(upper, lower);\r\n            upper = upper / divisor;\r\n            lower = lower / divisor;\r\n\r\n            return `${upper}/${lower}`;\r\n        }\r\n\r\n        // Function to update the result in real-time\r\n        function updateResult() {\r\n            const decimalInput = documen