(55, 2, 'Fraction to Percent Calculator', 'fraction-to-percent-calculator', '<form>\r\n            <div class=\"form-group\">\r\n                <label for=\"fraction\">Enter Fraction (e.g., 3/4):</label>\r\n                <input type=\"text\" class=\"form-control\" id=\"fraction\" placeholder=\"Enter fraction\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Result:</label>\r\n            <input type=\"text\" class=\"form-control\" id=\"result\" disabled>\r\n        </div>\r\n \r\n    <!-- Include Bootstrap JS and jQuery -->\r\n    <script src=\"https://code.jquery.com/jquery-3.5.1.slim.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/@popperjs/core@2.5.3/dist/umd/popper.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/bootstrap@4.5.2/dist/js/bootstrap.min.js\"></script>\r\n\r\n    <script>\r\n        // Function to convert a fraction to a percent\r\n        function fractionToPercent(fraction) {\r\n            const parts = fraction.split(\'/\');\r\n            if (parts.length !== 2) {\r\n                return \"Invalid input\";\r\n            }\r\n            const numerator = parseFloat(parts[0]);\r\n            const denominator = parseFloat(parts[1]);\r\n\r\n            if (isNaN(numerator) || isNaN(denominator) || denominator === 0) {\r\n                return \"Invalid input\";\r\n            }\r\n\r\n            const percent = (numerator / denominator) * 100;\r\n            return percent.toFixed(2) + \'%\';\r\n        }\r\n\r\n        // Function to update the result in real-time\r\n        function updateResult() {\r\n            const fractionInput = document.getElementById(\'fraction\');\r\n            const resultInput = document.getElementById(\'result\');\r\n\r\n            const fraction = fractionInput.value;\r\n\r\n            const result = fractionToPercent(fraction);\r\n            resultInput.value = result;\r\n        }\r\n\r\n    