(65, 4, 'Room Size Calculator', 'room-size-calculator', '<form>\r\n            <div class=\"form-group\">\r\n                <label for=\"length\">Enter Length (in feet):</label>\r\n                <input type=\"number\" step=\"0.01\" class=\"form-control\" id=\"length\" placeholder=\"Enter length\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"width\">Enter Width (in feet):</label>\r\n                <input type=\"number\" step=\"0.01\" class=\"form-control\" id=\"width\" placeholder=\"Enter width\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Room Size (in square feet):</label>\r\n            <input type=\"text\" class=\"form-control\" id=\"result\" disabled>\r\n        </div>\r\n  \r\n    <!-- Include Bootstrap JS and jQuery -->\r\n    <script src=\"https://code.jquery.com/jquery-3.5.1.slim.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/@popperjs/core@2.5.3/dist/umd/popper.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/bootstrap@4.5.2/dist/js/bootstrap.min.js\"></script>\r\n\r\n    <script>\r\n        // Function to calculate room size in square feet\r\n        function calculateRoomSize(length, width) {\r\n            if (isNaN(length) || isNaN(width) || length <= 0 || width <= 0) {\r\n                return \"Invalid input\";\r\n            }\r\n            const roomSize = length * width;\r\n            return roomSize.toFixed(2);\r\n        }\r\n\r\n        // Function to update the result in real-time\r\n        function updateResult() {\r\n            const lengthInput = document.getElementById(\'length\');\r\n            const widthInput = document.getElementById(\'width\');\r\n            const resultInput = document.getElementById(\'result\');\r\n\r\n            const length = parseFloat(lengthInput.value);\r\n            const width = parseFloat(widthInput.value);\r\n\r\n            const result = calculateRoomSize(length, width);\r\n            resultInput.value = result;\r\n        }\r\n\r\n        // Add event listeners to update the result while typing\r\n        const inputs = document.querySelectorAll(\'input\');\r\n        inputs.forEach(input => {\r\n            input.addEventListener(\'input\', updateResult);\r\n        });\r\n    </script>', '', '', '', '', 41, ''),
(66, 4, 'Fuel Cost Calculator', 'fuel-cost-calculator', '<form>\r\n            <div class=\"form-group\">\r\n                <label for=\"distance\">Enter Distance (in miles):</label>\r\n                <input type=\"number\" step=\"0.01\" class=\"form-control\" id=\"distance\" placeholder=\"Enter distance\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"mpg\">Enter MPG (Miles per Gallon):</label>\r\n                <input type=\"number\" step=\"0.01\" class=\"form-control\" id=\"mpg\" placeholder=\"Enter MPG\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"pricePerGallon\">Enter Fuel Price (per Gallon):</label>\r\n                <input type=\"number\" step=\"0.01\" class=\"form-control\" id=\"pricePerGallon\" placeholder=\"Enter fuel price\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Total Cost:</label>\r\n            <input type=\"text\" class=\"form-control\" id=\"result\" disabled>\r\n        </div>\r\n  \r\n    <!-- Include Bootstrap JS and jQuery -->\r\n    <script src=\"https://code.jquery.com/jquery-3.5.1.slim.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/@popperjs/core@2.5.3/dist/umd/popper.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/bootstrap@4.5.2/dist/js/bootstrap.min.js\"></script>\r\n\r\n    <script>\r\n        // Function to calculate fuel cost\r\n        function calculateFuelCost(distance, mpg, pricePerGallon) {\r\n            if (isNaN(distance) || isNaN(mpg) || isNaN(pricePerGallon) || distance <= 0 || mpg <= 0 || pricePerGallon <= 0) {\r\n                return \"Invalid input\";\r\n            }\r\n            const cost = (distance / mpg) * pricePerGallon;\r\n            return cost.toFixed(2);\r\n        }\r\n\r\n        // Function to update the result in real-time\r\n        function updateResult() {\r\n            const distanceInput = document.getElementById(\'distance\');\r\n            const mpgInput = document.getElementById(\'mpg\');\r\n            const pricePerGallonInput = document.getElementById(\'pricePerGallon\');\r\n            const resultInput = document.getElementById(\'result\');\r\n\r\n            const distance = parseFloat(distanceInput.value);\r\n            const mpg = parseFloat(mpgInput.value);\r\n            const pricePerGallon = parseFloat(pricePerGallonInput.value);\r\n\r\n            const result = calculateFuelCost(distance, mpg, pricePerGallon);\r\n            resultInput.value = result;\r\n        }\r\n\r\n        // Add event listeners to update the result while typing\r\n        const inputs = document.querySelectorAll(\'input\');\r\n        inputs.forEach(input => {\r\n            input.addEventListener(\'input\', updateResult);\r\n        });\r\n    </script>', '', '', '', '', 64, ''),
(67, 4, 'Shoe Size Calculator', 'shoe-size-calculator', '<form>\r\n            <div class=\"form-group\">\r\n                <label for=\"footLength\">Enter Foot Length (in inches):</label>\r\n                <input type=\"number\" step=\"0.01\" class=\"form-control\" id=\"footLength\" placeholder=\"Enter foot length\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"footWidth\">Enter Foot Width (in inches):</label>\r\n                <input type=\"number\" step=\"0.01\" class=\"form-control\" id=\"footWidth\" placeholder=\"Enter foot width\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Shoe Size (US Men\'s):</label>\r\n            <input type=\"text\" class=\"form-control\" id=\"result\" disabled>\r\n        </div>\r\n\r\n    <!-- Include Bootstrap JS and jQuery -->\r\n    <script src=\"https://code.jquery.com/jquery-3.5.1.slim.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/@popperjs/core@2.5.3/dist/umd/popper.min.js\"></script>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/bootstrap@4.5.2/dist/js/bootstrap.min.js\"></script>\r\n\r\n    <script>\r\n        // Function to calculate shoe size (US Men\'s)\r\n        function calculateShoeSize(footLength, footWidth) {\r\n            if (isNaN(footLength) || isNaN(footWidth) || footLength <= 0 || footWidth <= 0) {\r\n                return \"Invalid input\";\r\n            }\r\n            // Calculate shoe size based on a formula (replace with your specific formula)\r\n            const shoeSize = (footLength + footWidth) / 2;\r\n            return shoeSize.toFixed(2);\r\n        }\r\n\r\n        // Function to update the result in real-time\r\n        function updateResult() {\r\n            const footLengthInput = document.getElementById(\'footLength\');\r\n            const footWidthInput = document.getElementById(\'footWidth\');\r\n            const resultInput = document.getElementById(\'result\');\r\n\r\n            const footLength = parseFloat(footLengthInput.value);\r\n            const footWidth = parseFloat(footWidthInput.value);\r\n\r\n            const result = calculateShoeSize(footLength, footWidth);\r\n            resultInput.value = result;\r\n        }\r\n\r\n        // Add event listeners to update the result while typing\r\n        const inputs = document.querySelectorAll(\'input\');\r\n        inputs.forEach(input => {\r\n            input.addEventListener(\'input\', updateResult);\r\n        });\r\n    </script>', '', '', '', '', 49, '');
INSERT INTO `calculator` (`calculator_id`, `category_id`, `calculator_title`, `calculator_url`, `calculator_code`, `calculator_content`, `calculator_meta_title`, `calculator_meta_description`, `calculator_meta_keywords`, `calculator_views`, `calculator_status`) VALUES
(68, 5, 'Horse Power Calculator', 'horse-power-calculator', '<form id=\"calculator-form\">\r\n            <div class=\"form-group\">\r\n                <label for=\"torque\">Torque (lb-ft):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"torque\" placeholder=\"Enter torque\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"rpm\">RPM:</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"rpm\" placeholder=\"Enter RPM\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"horsepower\">Horsepower:</label>\r\n            <input type=\"text\" class=\"form-control\" id=\"horsepower\" disabled>\r\n        </div>\r\n    \r\n    <script>\r\n        // Function to calculate horsepower and update the result field\r\n        function calculateHorsepower() {\r\n            const torque = parseFloat(document.getElementById(\"torque\").value);\r\n            const rpm = parseFloat(document.getElementById(\"rpm\").value);\r\n            const horsepower = (torque * rpm) / 5252;\r\n            document.getElementById(\"horsepower\").value = isNaN(horsepower) ? \"\" : horsepower.toFixed(2);\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"torque\").addEventListener(\"input\", calculateHorsepower);\r\n        document.getElementById(\"rpm\").addEventListener(\"input\", calculateHorsepower);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n            const result = document.getElementById(\"horsepower\").value;\r\n            if (result) {\r\n                window.open(\"\", \"_blank\");\r\n                const resultWindow = window.open(\"\", \"_blank\");\r\n                resultWindow.document.write(\"<h1>Result:</h1>\");\r\n                resultWindow.document.write(`<p>Horsepower: ${result} hp</p>`);\r\n                resultWindow.document.close();\r\n            }\r\n        }\r\n\r\n        // Prevent the form from submitting on Enter key press\r\n        document.getElementById(\"calculator-form\").addEventListener(\"submit\", (e) => {\r\n            e.preventDefault();\r\n            openResultWindow();\r\n        });\r\n    </script>', '', '', '', '', 27, ''),
(69, 5, 'Momentum Calculator', 'momentum-calculator', '<form>\r\n            <div class=\"form-group\">\r\n                <label for=\"mass\">Mass (kg):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"mass\" placeholder=\"Enter mass\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"velocity\">Velocity (m/s):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"velocity\" placeholder=\"Enter velocity\" step=\"0.01\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"momentum\">Momentum (kgm/s):</label>\r\n            <input type=\"text\" class=\"form-control\" id=\"momentum\" disabled>\r\n        </div>\r\n\r\n    <script>\r\n        // Function to calculate momentum and update the result field\r\n        function calculateMomentum() {\r\n            const mass = parseFloat(document.getElementById(\"mass\").value);\r\n            const velocity = parseFloat(document.getElementById(\"velocity\").value);\r\n            const momentum = mass * velocity;\r\n            document.getElementById(\"momentum\").value = isNaN(momentum) ? \"\" : momentum.toFixed(2);\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"mass\").addEventListener(\"input\", calculateMomentum);\r\n        document.getElementById(\"velocity\").addEventListener(\"input\", calculateMomentum);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n            const result = document.getElementById(\"momentum\").value;\r\n            if (result) {\r\n                window.open(\"\", \"_blank\");\r\n                const resultWindow = window.open(\"\", \"_blank\");\r\n                resultWindow.document.write(\"<h1>Result:</h1>\");\r\n                resultWindow.document.write(`<p>Momentum: ${result} kgm/s</p>`);\r\n                resultWindow.document.close();\r\n            }\r\n        }\r\n\r\n        // Prevent the form from submitting on Enter key press\r\n        document.addEventListener(\"keydown\", function (e) {\r\n            if (e.key === \"Enter\") {\r\n                e.preventDefault();\r\n                openResultWindow();\r\n            }\r\n        });\r\n    </script>', '', '', '', '', 39, ''),
(70, 5, 'Projectile Motion Calculator', 'projectile-motion-calculator', ' <form>\r\n            <div class=\"form-group\">\r\n                <label for=\"initial-velocity\">Initial Velocity (m/s):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"initial-velocity\" placeholder=\"Enter initial velocity\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"launch-angle\">Launch Angle (degrees):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"launch-angle\" placeholder=\"Enter launch angle\" step=\"0.01\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Result:</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n    \r\n    <script>\r\n        // Function to calculate projectile motion result and update the result field\r\n        function calculateProjectileMotion() {\r\n            const initialVelocity = parseFloat(document.getElementById(\"initial-velocity\").value);\r\n            const launchAngle = parseFloat(document.getElementById(\"launch-angle\").value);\r\n\r\n            if (!isNaN(initialVelocity) && !isNaN(launchAngle)) {\r\n                const g = 9.81; // Acceleration due to gravity (m/s^2)\r\n                const radians = (launchAngle * Math.PI) / 180;\r\n                const timeOfFlight = (2 * initialVelocity * Math.sin(radians)) / g;\r\n                const maxHeight = (initialVelocity * initialVelocity * Math.sin(radians) * Math.sin(radians)) / (2 * g);\r\n                const horizontalRange = (initialVelocity * initialVelocity * Math.sin(2 * radians)) / g;\r\n\r\n                const resultText = `\r\n                    Time of Flight: ${timeOfFlight.toFixed(2)} seconds<br>\r\n                    Maximum Height: ${maxHeight.toFixed(2)} meters<br>\r\n                    Horizontal Range: ${horizontalRange.toFixed(2)} meters\r\n                `;\r\n\r\n                document.getElementById(\"result\").innerHTML = resultText;\r\n            } else {\r\n                document.getElementById(\"result\").innerHTML = \"\";\r\n            }\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"initial-velocity\").addEventListener(\"input\", calculateProjectileMotion);\r\n        document.getElementById(\"launch-angle\").addEventListener(\"input\", calculateProjectileMotion);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n            const result = document.getElementById(\"result\").innerHTML;\r\n            if (result) {\r\n                window.open(\"\", \"_blank\");\r\n                const resultWindow = window.open(\"\", \"_blank\");\r\n                resultWindow.document.write(\"<h1>Projectile Motion Result:</h1>\");\r\n                resultWindow.document.write(`<p>${result}</p>`);\r\n                resultWindow.document.close();\r\n            }\r\n        }\r\n\r\n        // Prevent the form from submitting on Enter key press\r\n        document.addEventListener(\"keydown\", function (e) {\r\n            if (e.key === \"Enter\") {\r\n                e.preventDefault();\r\n                openResultWindow();\r\n            }\r\n        });\r\n    </script>', '', '', '', '', 34, ''),
(71, 5, 'Wavelength Calculator', 'wavelength-calculator', ' <form>\r\n            <div class=\"form-group\">\r\n                <label for=\"speed-of-light\">Speed of Light (m/s):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"speed-of-light\" placeholder=\"Enter speed of light\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"frequency\">Frequency (Hz):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"frequency\" placeholder=\"Enter frequency\" step=\"0.01\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Result:</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n \r\n    <script>\r\n        // Function to calculate wavelength and update the result field\r\n        function calculateWavelength() {\r\n            const speedOfLight = parseFloat(document.getElementById(\"speed-of-light\").value);\r\n            const frequency = parseFloat(document.getElementById(\"frequency\").value);\r\n\r\n            if (!isNaN(speedOfLight) && !isNaN(frequency) && frequency !== 0) {\r\n                const wavelength = speedOfLight / frequency;\r\n                document.getElementById(\"result\").textContent = `Wavelength: ${wavelength.toFixed(2)} meters`;\r\n            } else {\r\n                document.getElementById(\"result\").textContent = \"\";\r\n            }\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"speed-of-light\").addEventListener(\"input\", calculateWavelength);\r\n        document.getElementById(\"frequency\").addEventListener(\"input\", calculateWavelength);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n            const result = document.getElementById(\"result\").textContent;\r\n            if (result) {\r\n                window.open(\"\", \"_blank\");\r\n                const resultWindow = window.open(\"\", \"_blank\");\r\n                resultWindow.document.write(\"<h1>Wavelength Calculator Result:</h1>\");\r\n                resultWindow.document.write(`<p>${result}</p>`);\r\n                resultWindow.document.close();\r\n            }\r\n        }\r\n\r\n        // Prevent the form from submitting on Enter key press\r\n        document.addEventListener(\"keydown\", function (e) {\r\n            if (e.key === \"Enter\") {\r\n                e.preventDefault();\r\n                openResultWindow();\r\n            }\r\n        });\r\n    </script>', '', '', '', '', 40, ''),
(72, 5, 'Center of Mass Calculator', 'center-of-mass-calculator', '   <form>\r\n            <div class=\"form-group\">\r\n                <label for=\"mass1\">Mass 1 (kg):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"mass1\" placeholder=\"Enter mass 1\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"position1\">Position 1 (m):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"position1\" placeholder=\"Enter position 1\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"mass2\">Mass 2 (kg):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"mass2\" placeholder=\"Enter mass 2\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"position2\">Position 2 (m):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"position2\" placeholder=\"Enter position 2\" step=\"0.01\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Center of Mass (m):</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n \r\n    <script>\r\n        // Function to calculate the center of mass and update the result field\r\n        function calculateCenterOfMass() {\r\n            const mass1 = parseFloat(document.getElementById(\"mass1\").value);\r\n            const position1 = parseFloat(document.getElementById(\"position1\").value);\r\n            const mass2 = parseFloat(document.getElementById(\"mass2\").value);\r\n            const position2 = parseFloat(document.getElementById(\"position2\").value);\r\n\r\n            if (!isNaN(mass1) && !isNaN(position1) && !isNaN(mass2) && !isNaN(position2)) {\r\n                const totalMass = mass1 + mass2;\r\n                const centerOfMass = (mass1 * position1 + mass2 * position2) / totalMass;\r\n                document.getElementById(\"result\").textContent = `Center of Mass: ${centerOfMass.toFixed(2)} meters`;\r\n            } else {\r\n                document.getElementById(\"result\").textContent = \"\";\r\n            }\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"mass1\").addEventListener(\"input\", calculateCenterOfMass);\r\n        document.getElementById(\"position1\").addEventListener(\"input\", calculateCenterOfMass);\r\n        document.getElementById(\"mass2\").addEventListener(\"input\", calculateCenterOfMass);\r\n        document.getElementById(\"position2\").addEventListener(\"input\", calculateCenterOfMass);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n            const result = document.getElementById(\"result\").textContent;\r\n            if (result) {\r\n                window.open(\"\", \"_blank\");\r\n                const resultWindow = window.open(\"\", \"_blank\");\r\n                resultWindow.document.write(\"<h1>Center of Mass Calculator Result:</h1>\");\r\n                resultWindow.document.write(`<p>${result}</p>`);\r\n                resultWindow.document.close();\r\n            }\r\n        }\r\n\r\n        // Prevent the form from submitting on Enter key press\r\n        document.addEventListener(\"keydown\", function (e) {\r\n            if (e.key === \"Enter\") {\r\n                e.preventDefault();\r\n                openResultWindow();\r\n            }\r\n        });\r\n    </script>\r\n', '', '', '', '', 44, ''),
(73, 5, 'Solar Panel Calculator', 'solar-panel-calculator', '<form>\r\n            <div class=\"form-group\">\r\n                <label for=\"solar-wattage\">Solar Panel Wattage (W):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"solar-wattage\" placeholder=\"Enter solar panel wattage\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"sunlight-hours\">Sunlight Hours per Day:</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"sunlight-hours\" placeholder=\"Enter sunlight hours per day\" step=\"0.01\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Estimated Daily Energy Production (Wh):</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n    \r\n    <script>\r\n        // Function to calculate estimated daily energy production and update the result field\r\n        function calculateEnergyProduction() {\r\n            const solarWattage = parseFloat(document.getElementById(\"solar-wattage\").value);\r\n            const sunlightHours = parseFloat(document.getElementById(\"sunlight-hours\").value);\r\n\r\n            if (!isNaN(solarWattage) && !isNaN(sunlightHours) && sunlightHours > 0) {\r\n                const dailyEnergyProduction = solarWattage * sunlightHours;\r\n                document.getElementById(\"result\").textContent = `Estimated Daily Energy Production: ${dailyEnergyProduction.toFixed(2)} Wh`;\r\n            } else {\r\n                document.getElementById(\"result\").textContent = \"\";\r\n            }\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"solar-wattage\").addEventListener(\"input\", calculateEnergyProduction);\r\n        document.getElementById(\"sunlight-hours\").addEventListener(\"input\", calculateEnergyProduction);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n            const result = document.getElementById(\"result\").textContent;\r\n            if (result) {\r\n                window.open(\"\", \"_blank\");\r\n                const resultWindow = window.open(\"\", \"_blank\");\r\n                resultWindow.document.write(\"<h1>Solar Panel Calculator Result:</h1>\");\r\n                resultWindow.document.write(`<p>${result}</p>`);\r\n                resultWindow.document.close();\r\n            }\r\n        }\r\n\r\n        // Prevent the form from submitting on Enter key press\r\n        document.addEventListener(\"keydown\", function (e) {\r\n            if (e.key === \"Enter\") {\r\n                e.preventDefault();\r\n                openResultWindow();\r\n            }\r\n        });\r\n    </script>', '', '', '', '', 47, ''),
(74, 5, 'Escape Velocity Calculator', 'escape-velocity-calculator', '<form>\r\n            <div class=\"form-group\">\r\n                <label for=\"mass\">Mass (kg):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"mass\" placeholder=\"Enter mass\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"radius\">Radius (m):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"radius\" placeholder=\"Enter radius\" step=\"0.01\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Escape Velocity (m/s):</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n   \r\n    <script>\r\n        // Function to calculate escape velocity and update the result field\r\n        function calculateEscapeVelocity() {\r\n            const gravitationalConstant = 6.67430e-11; // m^3/kg/s^2\r\n            const mass = parseFloat(document.getElementById(\"mass\").value);\r\n            const radius = parseFloat(document.getElementById(\"radius\").value);\r\n\r\n            if (!isNaN(mass) && !isNaN(radius) && radius > 0) {\r\n                const escapeVelocity = Math.sqrt((2 * gravitationalConstant * mass) / radius);\r\n                document.getElementById(\"result\").textContent = `Escape Velocity: ${escapeVelocity.toFixed(2)} m/s`;\r\n            } else {\r\n                document.getElementById(\"result\").textContent = \"\";\r\n            }\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"mass\").addEventListener(\"input\", calculateEscapeVelocity);\r\n        document.getElementById(\"radius\").addEventListener(\"input\", calculateEscapeVelocity);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n            const result = document.getElementById(\"result\").textContent;\r\n            if (result) {\r\n                window.open(\"\", \"_blank\");\r\n                const resultWindow = window.open(\"\", \"_blank\");\r\n                resultWindow.document.write(\"<h1>Escape Velocity Calculator Result:</h1>\");\r\n                resultWindow.document.write(`<p>${result}</p>`);\r\n                resultWindow.document.close();\r\n            }\r\n        }\r\n\r\n        // Prevent the form from submitting on Enter key press\r\n        document.addEventListener(\"keydown\", function (e) {\r\n            if (e.key === \"Enter\") {\r\n                e.preventDefault();\r\n                openResultWindow();\r\n            }\r\n        });\r\n    </script>', '', '', '', '', 44, ''),
(75, 6, 'Ideal Gas Law Calculator', 'ideal-gas-law-calculator', '<form>\r\n            <div class=\"form-group\">\r\n                <label for=\"pressure\">Pressure (Pa):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"pressure\" placeholder=\"Enter pressure\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"volume\">Volume (m):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"volume\" placeholder=\"Enter volume\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"temperature\">Temperature (K):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"temperature\" placeholder=\"Enter temperature\" step=\"0.01\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Result:</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n  \r\n    <script>\r\n        // Universal gas constant (R) in J/(molK)\r\n        const universalGasConstant = 8.314;\r\n\r\n        // Function to calculate the ideal gas law result and update the result field\r\n        function calculateIdealGasLaw() {\r\n            const pressure = parseFloat(document.getElementById(\"pressure\").value);\r\n            const volume = parseFloat(document.getElementById(\"volume\").value);\r\n            const temperature = parseFloat(document.getElementById(\"temperature\").value);\r\n\r\n            if (!isNaN(pressure) && !isNaN(volume) && !isNaN(temperature) && temperature > 0) {\r\n                const n = (pressure * volume) / (universalGasConstant * temperature);\r\n                document.getElementById(\"result\").textContent = `Amount of Gas (moles): ${n.toFixed(4)} moles`;\r\n            } else {\r\n                document.getElementById(\"result\").textContent = \"\";\r\n            }\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"pressure\").addEventListener(\"input\", calculateIdealGasLaw);\r\n        document.getElementById(\"volume\").addEventListener(\"input\", calculateIdealGasLaw);\r\n        document.getElementById(\"temperature\").addEventListener(\"input\", calculateIdealGasLaw);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n            const result = document.getElementById(\"result\").textContent;\r\n            if (result) {\r\n                window.open(\"\", \"_blank\");\r\n                const resultWindow = window.open(\"\", \"_blank\");\r\n                resultWindow.document.write(\"<h1>Ideal Gas Law Calculator Result:</h1>\");\r\n                resultWindow.document.write(`<p>${result}</p>`);\r\n                resultWindow.document.close();\r\n            }\r\n        }\r\n\r\n        // Prevent the form from submitting on Enter key press\r\n        document.addEventListener(\"keydown\", function (e) {\r\n            if (e.key === \"Enter\") {\r\n                e.preventDefault();\r\n                openResultWindow();\r\n            }\r\n        });\r\n    </script>', '', '', '', '', 0, ''),
(76, 6, 'Molar Mass Calculator', 'molar-mass-calculator', '<form>\r\n            <div class=\"form-group\">\r\n                <label for=\"element\">Chemical Element or Compound:</label>\r\n                <input type=\"text\" class=\"form-control\" id=\"element\" placeholder=\"Enter element or compound formula\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Molar Mass (g/mol):</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n \r\n    <script>\r\n        // Molar mass data for chemical elements\r\n        const molarMasses = {\r\n            H: 1.01,\r\n            He: 4.00,\r\n            Li: 6.94,\r\n            Be: 9.01,\r\n            B: 10.81,\r\n            C: 12.01,\r\n            N: 14.01,\r\n            O: 16.00,\r\n            F: 19.00,\r\n            Ne: 20.18,\r\n            // Add more elements and their molar masses as needed\r\n        };\r\n\r\n        // Function to calculate molar mass and update the result field\r\n        function calculateMolarMass() {\r\n            const elementInput = document.getElementById(\"element\").value.trim();\r\n            const elements = elementInput.split(/([A-Z][a-z]*)(\\d*)/).filter(Boolean);\r\n\r\n            let molarMass = 0;\r\n\r\n            for (let i = 0; i < elements.length; i += 2) {\r\n                const elementSymbol = elements[i];\r\n                const elementCount = elements[i + 1] ? parseInt(elements[i + 1]) : 1;\r\n\r\n                if (molarMasses[elementSymbol]) {\r\n                    molarMass += molarMasses[elementSymbol] * elementCount;\r\n                } else {\r\n                    // Handle invalid element symbols\r\n                    document.getElementById(\"result\").textContent = \"Invalid element symbol.\";\r\n                    return;\r\n                }\r\n            }\r\n\r\n            document.getElementById(\"result\").textContent = `Molar Mass: ${molarMass.toFixed(2)} 