(81, 6, 'Molarity Calculator', 'molarity-calculator', ' <form>\r\n            <div class=\"form-group\">\r\n                <label for=\"mass-solute\">Mass of Solute (g):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"mass-solute\" placeholder=\"Enter mass of solute\" step=\"0.01\">\r\n            </div>\r\n            <div class=\"form-group\">\r\n                <label for=\"volume-solution\">Volume of Solution (L):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"volume-solution\" placeholder=\"Enter volume of solution\" step=\"0.01\">\r\n            </div>\r\n        </form>\r\n        <div class=\"form-group\">\r\n            <label for=\"result\">Molarity (M):</label>\r\n            <p id=\"result\"></p>\r\n        </div>\r\n    \r\n    <script>\r\n        // Function to calculate molarity and update the result field\r\n        function calculateMolarity() {\r\n            const massSolute = parseFloat(document.getElementById(\"mass-solute\").value);\r\n            const volumeSolution = parseFloat(document.getElementById(\"volume-solution\").value);\r\n\r\n            if (!isNaN(massSolute) && !isNaN(volumeSolution) && volumeSolution > 0) {\r\n                const molarity = massSolute / volumeSolution;\r\n                document.getElementById(\"result\").textContent = `Molarity: ${molarity.toFixed(2)} M`;\r\n            } else {\r\n                document.getElementById(\"result\").textContent = \"\";\r\n            }\r\n        }\r\n\r\n        // Add event listeners to input fields for real-time calculation\r\n        document.getElementById(\"mass-solute\").addEventListener(\"input\", calculateMolarity);\r\n        document.getElementById(\"volume-solution\").addEventListener(\"input\", calculateMolarity);\r\n\r\n        // Function to open a new window with the result\r\n        function openResultWindow() {\r\n            const result = document.getElementById(\"result\").textContent;\r\n  