w.document.write(\"<h1>Density Calculator Result:</h1>\");\r\n                resultWindow.document.write(`<p>${result}</p>`);\r\n                resultWindow.document.close();\r\n            }\r\n        }\r\n\r\n        // Prevent the form from submitting on Enter key press\r\n        document.addEventListener(\"keydown\", function (e) {\r\n            if (e.key === \"Enter\") {\r\n                e.preventDefault();\r\n                openResultWindow();\r\n            }\r\n        });\r\n    </script>', '', '', '', '', 70, '');
