(102, 1, 'bmi', 'bmi', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n  <meta charset=\"UTF-8\">\r\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n  <title>BMI Calculator</title>\r\n  <style>\r\n    body {\r\n      font-family: Arial, sans-serif;\r\n    }\r\n    .form-group {\r\n      margin-bottom: 20px;\r\n    }\r\n    input[type=\"number\"] {\r\n      padding: 8px;\r\n      width: 100%;\r\n      box-sizing: border-box;\r\n    }\r\n    button {\r\n      padding: 10px 20px;\r\n      background-color: #007bff;\r\n      color: white;\r\n      border: none;\r\n      cursor: pointer;\r\n    }\r\n    button:hover {\r\n      background-color: #0056b3;\r\n    }\r\n    #resultContainer {\r\n      margin-top: 20px;\r\n      font-weight: bold;\r\n    }\r\n  </style>\r\n</head>\r\n<body>\r\n  <form method=\"post\" class=\"mt-4\" id=\"bmiCalculatorForm\">\r\n    <div class=\"form-group\">\r\n      <label for=\"height\">Enter your height (in cm):</label>\r\n      <input type=\"number\" class=\"form-control\" name=\"height\" id=\"height\">\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label for=\"weight\">Enter your weight (in kg):</label>\r\n      <input type=\"number\" class=\"form-control\" name=\"weight\" id=\"weight\">\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <button type=\"button\" class=\"btn btn-primary\" id=\"calculateBtn\">Calculate BMI</button>\r\n    </div>\r\n  </form>\r\n  <div id=\"resultContainer\"></div>\r\n\r\n  <script>\r\n    document.getElementById(\"calculateBtn\").addEventListener(\"click\", function() {\r\n      var height = parseFloat(document.getElementById(\"height\").value);\r\n      var weight = parseFloat(document.getElementById(\"weight\").value);\r\n\r\n      if (isNaN(height) || isNaN(weight) || height <= 0 || weight <= 0) {\r\n        document.getElementById(\"resultContainer\").innerHTML = \'Please enter valid height and weight.\';\r\n        return;\r\n      }\r\n\r\n      var bmi = weight / ((height / 100) * (height / 100));\r\n      var bmiResult = \'Your BMI is \' + bmi.toFixed(2) + \'. \';\r\n\r\n      if (bmi < 18.5) {\r\n        bmiResult += \'You are underweight.\';\r\n      } else if (bmi >= 18.5 && bmi < 25) {\r\n        bmiResult += \'You have a normal weight.\';\r\n      } else if (bmi >= 25 && bmi < 30) {\r\n        bmiResult += \'You are overweight.\';\r\n      } else {\r\n        bmiResult += \'You are obese.\';\r\n      }\r\n\r\n      document.getElementById(\"resultContainer\").innerHTML = bmiResult;\r\n    });\r\n  </script>\r\n</body>\r\n</html>\r\n', '', '', '', '', 76, ''),
