(105, 6, 'Atom Calculator', 'atom-calculator', 'import React from \'react\';\r\nimport { Card, CardContent } from \'@/components/ui/card\';\r\nimport { Button } from \'@/components/ui/button\';\r\nimport { Input } from \'@/components/ui/input\';\r\nimport { motion } from \'framer-motion\';\r\n\r\nconst AtomCalculatorApp = () => {\r\n  return (\r\n    <div className=\"min-h-screen bg-gray-100 p-4 flex flex-col items-center\">\r\n      <motion.div\r\n        initial={{ opacity: 0, y: -20 }}\r\n        animate={{ opacity: 1, y: 0 }}\r\n        transition={{ duration: 0.5 }}\r\n        className=\"text-center mb-6\"\r\n      >\r\n        <h1 className=\"text-4xl font-bold text-gray-800\">Atom Calculator</h1>\r\n        <p className=\"text-lg text-gray-600 mt-2\">\r\n          Simplify atomic calculations with precision.\r\n        </p>\r\n      </motion.div>\r\n\r\n      <Card className=\"w-full max-w-md shadow-xl bg-white rounded-2xl p-4\">\r\n        <CardContent>\r\n          <div className=\"mb-4\">\r\n            <label htmlFor=\"element\" className=\"block text-gray-700 font-medium mb-2\">\r\n              Enter Atomic Symbol\r\n            </label>\r\n            <Input id=\"element\" placeholder=\"E.g., H, O, C\" className=\"w-full\" />\r\n          </div>\r\n\r\n          <div className=\"mb-4\">\r\n            <label htmlFor=\"atomic-number\" className=\"block text-gray-700 font-medium mb-2\">\r\n              Enter Atomic Number\r\n            </label>\r\n            <Input id=\"atomic-number\" type=\"number\" placeholder=\"E.g., 1, 8, 6\" className=\"w-full\" />\r\n          </div>\r\n\r\n          <div className=\"text-center mt-6\">\r\n            <Button className=\"w-full bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-2xl text-lg font-medium\">\r\n              Calculate\r\n            </Button>\r\n          </div>\r\n        </CardContent>\r\n      </Card>\r\n\r\n      <motion.div\r\n        initial={{ opacity: 0, y: 20 }}\r\n        animate={{ opacity: 1, y: 0 }}\r\n        transition={{ duration: 0.5 }}\r\n        className=\"mt-8 text-center\"\r\n      >\r\n        <h2 className=\"text-2xl font-semibold text-gray-800\">Results</h2>\r\n        <p className=\"text-gray-600 mt-2\">Results will appear here after calculation.</p>\r\n      </motion.div>\r\n    </div>\r\n  );\r\n};\r\n\r\n\r\n', '', '', '', '', 1, 'draft'),
(106, 8, 'Board Foot Calculator', 'board-foot-calculator', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <title>Board Foot Calculator</title>\r\n    <link rel=\"stylesheet\" href=\"https://cdn.jsdelivr.net/npm/bootstrap@5.3.0-alpha1/dist/css/bootstrap.min.css\">\r\n</head>\r\n<body>\r\n    <div class=\"container mt-5\">\r\n        <h1 class=\"text-center\">Board Foot Calculator</h1>\r\n        <form id=\"boardFootCalculatorForm\" class=\"mt-4\">\r\n            <div class=\"mb-3\">\r\n                <label for=\"thickness\" class=\"form-label\">Thickness (in inches):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"thickness\" placeholder=\"Enter thickness\">\r\n            </div>\r\n            <div class=\"mb-3\">\r\n                <label for=\"width\" class=\"form-label\">Width (in inches):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"width\" placeholder=\"Enter width\">\r\n            </div>\r\n            <div class=\"mb-3\">\r\n                <label for=\"length\" class=\"form-label\">Length (in feet):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"length\" placeholder=\"Enter length\">\r\n            </div>\r\n            <button type=\"button\" class=\"btn btn-primary\" id=\"calculateBtn\">Calculate</button>\r\n        </form>\r\n\r\n        <div id=\"resultContainer\" class=\"mt-4\"></div>\r\n    </div>\r\n\r\n    <script>\r\n        document.getElementById(\"calculateBtn\").addEventListener(\"click\", function() {\r\n            // Get input values\r\n            const thickness = parseFloat(document.getElementById(\"thickness\").value);\r\n            const width = parseFloat(document.getElementById(\"width\").value);\r\n            const length = parseFloat(document.getElementById(\"length\").value);\r\n\r\n            // Validate inputs\r\n            if (isNaN(thickness) || isNaN(width) || isNaN(length) || thickness <= 0 || width <= 0 || length <= 0) {\r\n                document.getElementById(\"resultContainer\").innerHTML = \'<div class=\"alert alert-danger\">Please enter valid positive numbers for all fields.</div>\';\r\n                return;\r\n            }\r\n\r\n            // Calculate board feet\r\n            const boardFeet = (thickness * width * length) / 12;\r\n\r\n            // Display the result\r\n            document.getElementById(\"resultContainer\").innerHTML = `\r\n                <div class=\"alert alert-success\">\r\n                    <h4>Board Feet:</h4>\r\n                    <h2>${boardFeet.toFixed(2)} ft<sup>3</sup></h2>\r\n                </div>\r\n            `;\r\n        });\r\n    </script>\r\n</body>\r\n</html>\r\n', '', '', '', '', 2, 'publish'),
(107, 1, 'Board Foot Calculator', 'board-foot-calculator', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <title>Board Foot Calculator</title>\r\n    <link rel=\"stylesheet\" href=\"https://cdn.jsdelivr.net/npm/bootstrap@5.3.0-alpha1/dist/css/bootstrap.min.css\">\r\n    <style>\r\n        .calculator-container {\r\n            max-width: 500px;\r\n            margin: 20px auto;\r\n            background: #f8f9fa;\r\n            border-radius: 8px;\r\n            padding: 20px;\r\n            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);\r\n        }\r\n        .section-title {\r\n            font-weight: bold;\r\n            margin-top: 20px;\r\n        }\r\n    </style>\r\n</head>\r\n<body>\r\n    <div class=\"container calculator-container\">\r\n        <h1 class=\"text-center\">Board Foot Calculator</h1>\r\n\r\n        <!-- Input Fields -->\r\n        <form id=\"boardFootCalculatorForm\" class=\"mt-4\">\r\n            <div class=\"mb-3\">\r\n                <label for=\"pieces\" class=\"form-label\">No. of pieces:</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"pieces\" value=\"1\">\r\n            </div>\r\n            <div class=\"mb-3\">\r\n                <label for=\"thickness\" class=\"form-label\">Thickness (in inches):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"thickness\" placeholder=\"Enter thickness\">\r\n            </div>\r\n            <div class=\"mb-3\">\r\n                <label for=\"width\" class=\"form-label\">Width (in inches):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"width\" placeholder=\"Enter width\">\r\n            </div>\r\n            <div class=\"mb-3\">\r\n                <label for=\"length\" class=\"form-label\">Length (in feet):</label>\r\n                <input type=\"number\" class=\"form-control\" id=\"length\" p